import React, { useState } from "react";

import ExpenseForm from "./ExpenseForm";
import './NewExpense.css'

const NewExpense = (props) => {
    const saveExpenseDataHandler = (enteredExpenseData) => {
        const expenseData = {
            ...enteredExpenseData,
            id: Math.random().toString()
        }
        props.onAddExpense(expenseData)
    }

    const [formContent, setFormContent] = useState(<button onClick={displayForm}>Add new expense</button>)

    const displayForm = () => {
        if (formContent === <button onClick={displayForm}>Add new expense</button>) {
            setFormContent(<ExpenseForm onSaveExpenseData={saveExpenseDataHandler} />)
        } else {
            setFormContent('')
        }
    }

    return (
        <div className='new-expense'>
            {formContent}
        </div>
    )
};

export default NewExpense;
